"use client";
import React, { useState } from "react";
import scss from "@/components/pages/ArticleAfter/SectionComent.module.scss";

const SectionComent = () => {
  const [comments, setComments] = useState([
    {
      author: "Осмонова Нурай",
      date: "Около минуты назад",
      text: "Очень понравились уроки. Все более чем понятно!",
      avatar:
        "data:image/jpeg;base64,", // пример ссылки на аватар
    },
    {
      author: "Эгембердиева Кунсулуу",
      date: "24.10.2021",
      text: "Классный урок, первый раз вижу такое подробное объяснение всего происходящего.",
      avatar: "data:image/jpeg;base64,",
    },
    {
      author: "Талай Бирдемке",
      date: "24.10.2021",
      text: "Как мне кажется, в конце видео, необходимо было сказать о том, что информация...",
      avatar: "data:image/jpeg;base64,",
    },
  ]);

  const [newComment, setNewComment] = useState("");
  const [showAll, setShowAll] = useState(false);

  const handleAddComment = () => {
    if (newComment.trim()) {
      const newCommentData = {
        author: "Вы",
        date: "Только что",
        text: newComment,
        avatar: "data:image/jpeg;base64,", // Аватар для новых комментариев
      };
      setComments([newCommentData, ...comments]);
      setNewComment("");
    }
  };

  const handleShowMore = () => {
    setShowAll(!showAll);
  };

  return (
    <div id={scss.sectionComent}>
      <div className="container">
        {/* Input box for adding a comment */}
        <div className={scss.commentInputBox}>
          <textarea
            className={scss.textArea}
            placeholder="Ваш комментарий"
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
          />
          <button className={scss.submitButton} onClick={handleAddComment}>
            Отправить
          </button>
        </div>

        {/* List of comments */}
        <div className={scss.commentList}>
          {comments
            .slice(0, showAll ? comments.length : 3)
            .map((comment, index) => (
              <div key={index} className={scss.commentItem}>
                <div className={scss.commentHeader}>
                  <img
                    src={comment.avatar}
                    alt="Avatar"
                    className={scss.avatar}
                  />
                  <div>
                    <span className={scss.author}>{comment.author}</span>
                    <span className={scss.date}>{comment.date}</span>
                  </div>
                </div>
                <p className={scss.commentText}>{comment.text}</p>
                <button className={scss.replyButton}>Ответить</button>
              </div>
            ))}
        </div>

        {/* Show more button */}
        {comments.length > 3 && (
          <button className={scss.showMoreButton} onClick={handleShowMore}>
            {showAll ? "Скрыть" : "Смотреть все"}
          </button>
        )}
      </div>
    </div>
  );
};

export default SectionComent;
